    public static class loginUser{
        private static String cell;
        private static String user;
        private static String password;
        static String getUserCell(){
            return cell;
        }
        static String getUser(){
            return user;
        }
        static String getPassword(){
            return password;
        }
        static void setUserCell(String num){
           loginUser.cell=num;
        }
        static void setUser(String name){
           loginUser.user=name;
        }
        static void setPassword(String pass){
            loginUser.password=pass;
        }
    }
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        
            if(!cell.getText().isEmpty() && !password.getText().isEmpty()){
                String cel = cell.getText();
                String pass = password.getText();
                loginUser.setUserCell(cel);                loginUser.setPassword(pass);

                String sql = "select * from accounts where cell = "+cel+" and pass = '"+pass+"'";
                Connection con;
                Statement st;
                ResultSet rs;
                 try{
                        con = JDBC.getConnection();
                        st = con.createStatement();
                        rs = st.executeQuery(sql);
                        if(rs.next()){
                                loginUser.setUser(rs.getString("name")); 
                                JOptionPane.showMessageDialog(null,"You are now logged in");
                                HomePage homePageFrame=new HomePage();
                                homePageFrame.setVisible(true);
                                homePageFrame.pack();
                                homePageFrame.setLocationRelativeTo(null);
                                dispose();
                        }
                        else{
                            JOptionPane.showMessageDialog(this,"Incorrect username or pass","ERROR",JOptionPane.ERROR_MESSAGE);
                        }
                        con.close();
                        st.close();
                        rs.close();
                   }
               catch(Exception e){
                   JOptionPane.showMessageDialog(this,"Please enter correct number and password!");
                   System.out.println("Error while connecting database.."+e.getMessage());
               }
                
            }
            else{
                if(cell.getText().isEmpty() || password.getText().isEmpty()){
                   JOptionPane.showMessageDialog(this,"Please fill the form!","ERROR",JOptionPane.ERROR_MESSAGE);
                }
                else
                {
                    JOptionPane.showMessageDialog(this,"Incorrect username or pass","ERROR",JOptionPane.ERROR_MESSAGE);
                     cell.setText("");
                    password.setText("");
                }
            }
       


        // TODO add your handling code here:
        
    }//GEN-LAST:event_jButton2ActionPerformed

