    private void cellKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_cellKeyTyped
        // TODO add your handling code here:
        
        
        char c = evt.getKeyChar();
        if(!Character.isDigit(c)){
            evt.consume();
        }
    }//GEN-LAST:event_cellKeyTyped

