    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(!txtName.getText().isEmpty() && !txtPass.getText().isEmpty() && !txtCell.getText().isEmpty() && !txtEmail.getText().isEmpty()){
            int res = JOptionPane.showConfirmDialog(this, "Are you sure to create account?", "CONFIRM", JOptionPane.YES_NO_OPTION);
              if(res==0){                 
            String name = txtName.getText();
                String pass = txtPass.getText();
                int cell =  Integer.parseInt(txtCell.getText());
                String email = txtEmail.getText();
                login.loginUser.setUserCell(String.valueOf(cell));
               login.loginUser.setUser(name);
                login.loginUser.setPassword(pass);

                String sql1 = "select * from accounts where cell = "+cell+"";
                String sql2 = "select * from accounts where email = '"+email+"'";
                try{
                        Connection con;
                        con = JDBC.getConnection();
                        ResultSet rs1;
                try (Statement st = con.createStatement()) {
                    rs1 = st.executeQuery(sql1);
                    if(!rs1.next())
                    {
                        ResultSet rs2 = st.executeQuery(sql2);
                        if(!rs2.next()){
                            String queryToInsert = "insert into accounts values (default,'"+name+"', '"+pass+"',"+cell+",'"+email+"')";
                            st.execute(queryToInsert);
                            //Account has been created
                            JOptionPane.showMessageDialog(this, "Account has been created successfully!");
                            HomePage homePageFrame=new HomePage();
                            homePageFrame.setVisible(true);
                            homePageFrame.pack();
                            homePageFrame.setLocationRelativeTo(null);
                            rs2.close();
                            dispose();
                        }
                        else{
                            JOptionPane.showMessageDialog(this,"Email already used!","ERROR",JOptionPane.ERROR_MESSAGE);
                            txtEmail.setText("");
                            
                        }
                    }
                    else{
                        JOptionPane.showMessageDialog(this,"Phone number already used!","ERROR",JOptionPane.ERROR_MESSAGE);
                        txtCell.setText("");
                    }
                    con.close();
                }
                        rs1.close();
                   }
               catch(HeadlessException | SQLException e){
                    JOptionPane.showMessageDialog(this,"Something went Wrong! Try again...","ERROR",JOptionPane.ERROR_MESSAGE);
                    System.out.println("Error while connecting database.."+e.getMessage());
               }
              }
            }
            else{
                if(txtName.getText().isEmpty() || txtPass.getText().isEmpty() || txtCell.getText().isEmpty() || txtEmail.getText().isEmpty()){
                   JOptionPane.showMessageDialog(this,"Please fill the form!","ERROR",JOptionPane.ERROR_MESSAGE);
                }
            }
    }//GEN-LAST:event_jButton1ActionPerformed

